import React, { Component } from 'react';
import Filter from './Filter';


class Cities extends Component {

    state = {
        cities: [],
        filteredCities:[]
    }

    fetchCities = async () => {
        var url = "https://mern-ubiqum-v2.herokuapp.com/cities/all"
        let data = await fetch(url, {
            method: "GET",
        })
            .then(response => response.json())
            .then(data => {
                console.log(data)
                return data
            })
            .catch(error => console.log(error))
        return data
    }

    componentDidMount() {
        this.fetchCities()
            .then((cities) => this.setState({ cities }))
        // .then(() => console.log('cities from state: ', this.state.cities)

    }

    filterCities = (cityFilter) => { 
        let filteredCities = this.state.cities.filter((city)=>{
            let cityName = city.name.toLowerCase()
            return cityName.indexOf(
                cityFilter.toLowerCase()) !==-1
        })
        this.setState({
            filteredCities
        })
    }

    render() {
        const { cities } = this.state;
        let filteredCities = cities.filter((city) =>{
            return city.name.toLowerCase().indexOf(this.state.cityFilter.toLowerCase())!== -1 
        },
    ); 


        // console.log(this.state)
        const cityList = filteredCities.map((city) => {
            return (
                <li className="city" key={city._id}>{city.name}</li>
            )
        }
        )

        return (


                <div>
                    <Filter/>
                    <ul className="city-list">
                        {cityList}
                    </ul>
                </div>

        )
    }
}


export default Cities
