    //         return cityName.indexOf(
    //             cityFilter.toLowerCase()) !==-1
    //     })
    //     this.setState({
    //         filteredCities
    //     })
    // }

    render() {

        let filteredCities = this.props.cities.filter((city) =>{
            return city.name.toLowerCase().indexOf(this.state.cityFilter.toLowerCase())!== -1 
        },
        this.setState({filteredCities})
    ); 

        const { cities } = this.state;
        // console.log(this.state)
        const cityList = cities.map((city) => {
            return (
                <li className="city" key={city._id}>{city.name}</li>
            )
        }
        )

        return (


                <div>
                    <Filter/>
                {/* <Cities cities = {this.state.filteredCities} 
                  match = {this.props.match} 
                  onChange={this.filterCities}/> */}
                    <ul className="city-list">
                        {cityList}
                    </ul>
                </div>

        )
    }
}


export default Cities
