import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import homeIcon from '../images/homeIcon.png'



class Cities extends Component {

    constructor(props) {
        super(props);
        this.state = {
            cities: [],
            cityFilter: "",
        }
    }

    componentDidMount() {
        this.fetchCities()
            .then((cities) => this.setState({ cities }))
    }

    handleChange = (e) => {
        this.setState({
            cityFilter: e.target.value
        })
    }

    // fetchCities = async () => {
