const FETCH_ACTIVITIES_REQUEST = 'FETCH_ACTIVITIES_REQUEST'
const FETCH_ACTIVITIES_SUCCESS = 'FETCH_ACTIVITIES_SUCCESS'
const FETCH_ACTIVITIES_FAILURE = 'FETCH_ACTIVITIES_FAILURE'

const initialState = {
    loading: true,
    activities: [],
    error: ''
}

export default function ActivitiesReducer (state= initialState, action) {
    switch (action.type){
        case FETCH_ACTIVITIES_REQUEST:
            return {
                ...state,
                loading: true
            }

        case FETCH_ACTIVITIES_SUCCESS:
            return {
                loading: false,
                activities: action.payload,
                error: ''
            }  

        case FETCH_ACTIVITIES_FAILURE:
            return {
                loading: false, 
                activities: [],
                error: action.payload
            } 

        default:
            return state    
    }
}
