import React, { Component } from 'react';
import {connect} from 'react-redux';
import {createAccount} from '../store/actions/newAccountActions'
// import { Link } from 'react-router-dom';
// import homeIcon from '../images/homeIcon.png';


 class NewAccount extends Component {

    constructor (props) {
        super (props);
        this.state = {
            username:'',
            password:'',
            email:'',
            firstName:'',
            lastName:'',
            country:'',
            isAgree: true
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleInputChange = this.handleInputChange.bind(this);
    }

   componentDidMount(){
       this.props.createAccount()
   }

    handleChange = (e) => {
        this.setState ({
            value: e.target.value,
            name: e.target.name
            });
    }

    handleSubmit = (e) => {
        alert('You are creating a new account' + this.state.value);
        e.preventDefault();
    }

    handleInputChange = (e) => {
        const target = e.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;

        this.setState({
            [name]: value
        });
    }

    render() {
        console.log('create account, dude!')
        return (

            <div className="form-container">

                <form onSubmit ={this.handleSubmit}>

                    <div className="userDetailsItem">
                    <label>
                        Username:
                        <input name='username' type='text' value={this.state.value} onChange={this.handleChange}/>
                    </label>
                    <label>
                        Password:
                        <input name='password' type='text' value={this.state.value} onChange={this.handleChange}/>
                    </label>
                    <label>
                        Email:
                        <input name='email' type='email' value={this.state.value} onChange={this.handleChange}/>
                    </label>
                    <label>
                        Firstname:
                        <input name='firstName' type='text' value={this.state.value} onChange={this.handleChange}/>
                    </label>
                    <label>
                        Lastname:
                        <input name='lastName' type='text' value={this.state.value} onChange={this.handleChange}/>
                    </label>
                    <label>
                        Country:
                        <select name='country' value={this.state.value} onChange={this.handleChange}>
                            <option value='England'>England</option>
                            <option value='France'>France</option>
                            <option value='Germany'>Germany</option>
                            <option value='Holland'>Holland</option>
                            <option value='Ireland'>Ireland</option>
                            <option value='Spain'>Spain</option>
                            <option value='United States'>United States</option>
                        </select>
                        </label>
                        </div>
                        <label> 
                        <input name='isAgree'  type='checkbox' 
                        checked={this.state.isAgree} onChange={this.handleInputChange}/> 
                        I agree to Terms & Conditions
                        </label>
                        <input type="submit" value="OK" />
                </form> 

            </div>
        )
    }
}

const mapStateToProps = state => {
    return {
        newAccount: state.newAccount,
        loading: state.newAccount.loading
    }
}

const mapDispatchToProps = dispatch => {
    return {
        createAccount: () => dispatch(createAccount())
    }
}

export default connect (
    mapStateToProps,
    mapDispatchToProps
) (NewAccount)
