const FETCH_ITINERARY_REQUEST = 'FETCH_ITINERARY_REQUEST'
const FETCH_ITINERARY_SUCCESS = 'FETCH_ITINERARY_SUCCESS'
const FETCH_ITINERARY_FAILURE = 'FETCH_ITINERARY_FAILURE'


const initialStateItinerary = {
    loading: true, 
    itineraries: [],
    error: ''
}


function itineraryReducer (state = initialStateItinerary, action) {
    switch (action.type){
        case FETCH_ITINERARY_REQUEST:
            return {
                ...state,
                loading: true
            }

        case FETCH_ITINERARY_SUCCESS:
            return {
                loading: false, 
                itineraries: action.payload, 
                error: ''
            }  

        case FETCH_ITINERARY_FAILURE:
            return {
                loading: false, 
                itineraries: [],
                error: action.payload
            }    

        default:
            return state    
    }
}

export default itineraryReducer
