import React, { Component } from 'react';
import { connect } from 'react-redux'
import { fetchItinerariesList } from '../store/actions/itinerariesActions'
// import { Link } from 'react-router-dom';



class Itineraries extends Component {

    constructor(props) {
        super(props);
        this.state = {
            itineraries: [],
            city:"",
            fetchItineraryList: []
        }
    }

    componentDidMount() {
        let city = window.location.pathname.split('/')[2]
        this.props.fetchItinerariesList(city);
    }


    render() {

        const {itineraries} =this.props;
        console.log(itineraries)
        console.log(this.props)

        const { loading } = this.props;

        if (!loading)
    return (
        <div className="flex-container">

            <div className="itineraries-list">

              <ul>
                  {this.fetchItinerariesList(city).map((itineraries)=>{
                      return (
                        <li className="city" key={itineraries._id}>
                      <h2>{this.state.city}</h2>

                  </li>
                      )
                  })}


              </ul>

            </div>

        </div>
    )
    else
    return (
        <div>Loading itineraries...</div>
    )
}
}
const mapStateToProps = state => {
    return {
        itineraries: state.itineraries.itineraries,
        loading: state.itineraries.loading
    }
}

const mapDispatchToProps = dispatch => {
    return {
        fetchItinerariesList: (city) => dispatch(fetchItinerariesList(city))
    }
}


export default connect(
    mapStateToProps,
    mapDispatchToProps
)(Itineraries)
