import React, { Component } from 'react';
import {connect} from 'react-redux'
import { Link } from 'react-router-dom';
import homeIcon from '../images/homeIcon.png'
import {citiesList} from '../store/reducers/cityReducer'

const mapStateToProps = state => {
    return {
        initialState: state.initialState
    }
}

const mapDispatchToProps = dispatch => {
    return {
        citiesList: () => dispatch (citiesList)
    }
}



class Cities extends Component {

    constructor(props) {
        super(props);
        this.state = {
            cities: [],
            cityFilter: "",
        }
    }

    // componentDidMount() {
    //     this.fetchCities()
    //         .then((cities) => this.setState({ cities }))
    // }

    handleChange = (e) => {
        this.setState({
            cityFilter: e.target.value
        })
    }

    // fetchCities = async () => {
    //     var url = "https://mern-ubiqum-v2.herokuapp.com/cities/all"
    //     return await fetch(url, {
    //         method: "GET",
    //     })
    //         .then(response => response.json())
    //         .then(data => data)
    //         .catch(error => console.log(error))
    // }

    filterCities = () => {
        const { cities, cityFilter } = this.state;
        return cities.filter((city) => {
            return city.name.toLowerCase().indexOf(cityFilter.toLowerCase()) !== -1
        },
        );
    }

    render() {
        return (
            <div className="flex-container">
                <header >
                 <div></div>
                </header>

                <div className="filter">
                    <label htmlFor="filter">Filter by City: </label>
                    <input type="text" id="filter"
                        value={this.state.cityFilter}
                        onChange={this.handleChange.bind(this)} />

                </div>

                <cityAction/>



                <div className="city-list">
                    <ul >
                        {this.filterCities().map((city) => {
                            return (
                                <li className="city" key={city._id}>{city.name}</li>
                            )
                        }
                        )}
                    </ul>
                </div>

                <div className="itemFooter">
                    <Link to="/">
                        <img src={homeIcon} alt="homeLogo" />
                    </Link>
                </div>

            </div>
        )
    }
}


export default connect(
    mapStateToProps, 
    mapDispatchToProps
    ) (citiesList)
