//  3- REDUCERS

// 0- create ReduxStore

const redux = require('redux')
const createStore = redux.createStore
const applyMiddleware = redux.applyMiddleware
const thunkMiddleware = require('redux-thunk').default
const axios = require('axios')


import {combineReducers} from 'redux'
import {
    FETCH_CITIES_REQUEST,
    FETCH_CITIES_SUCCESS,
    FETCH_CITIES_FAILURE
} from '../actions/cityActions'

// 1- STATE

const initialState = {
    loading: false, 
    cities: [],
    error: ''
}

const citiesReducer = (state = initialState, action) => {
    switch (action.type){
        case FETCH_CITIES_REQUEST:
            return {
                ...state,
                loading: true
            }

        case FETCH_CITIES_SUCCESS:
            return {
                loading: false, 
                cities: action.payload,
                error: ''
            }    

        case FETCH_CITIES_FAILURE:
            return {
                loading: false, 
                cities: [],
                error: action.payload
            }    
    }
}

// Action Creators

const citiesList = () => {
    return function(dispatch) {

        dispatch(fetchCitiesRequest())
        axios.get('https://mern-ubiqum-v2.herokuapp.com/cities/all')
        .then (response => {
            const cities = response.data.map(city => city.id)
            dispatch(fetchCitiesSuccess(cities))
        })

        .catch (error => {
            dispatch(fetchCitiesFailure(error.message))
        })
    }
}

const store = createStore(citiesReducer, applyMiddleware(thunkMiddleware))

// now we subscribe the store

store.subscribe(()=> {console.log(store.getState())})
store.dispatch(citiesList())

export default (citiesReducer, citiesList)
