import React, { Component } from 'react';
import { connect } from 'react-redux'
import { fetchItinerariesList } from '../store/actions/itinerariesActions'
// import { Link } from 'react-router-dom';



class Itineraries extends Component {

    constructor(props) {
        super(props);
        this.state = {
            itineraries: [],
            city:"",
            itinerariesByCity: [],
            fetchItineraryList: []
        }
    }

    componentDidMount() {
        this.props.fetchItinerariesList();
        let cityItineraries = this.props.itineraries.itineraries.city;
        this.setState({
            cityItineraries : cityItineraries
        });
    }

    itinerariesByCity = () => {
        const itineraries = this.props.itineraries;
        // const city = this.props.itineraries.city
        const {cityItineraries} = this.state;
        return itineraries.findOne( city => {
            return cityItineraries
        },

        )}
    // itinerariesByCity = () => {

    // itineraries.findOne({city}, function(err, result){
    //     if (err) throw err;
    //     console.log(result.itineraries. city)
    // })

    // }

    render() {
        return (
            <div className="flex-container">

                <div className="itineraries-list">

                  <ul>
                      <li className="itinerary" key={this.itinerariesByCity._id}>
                          {this.state.cityItineraries}
                      </li>
                  </ul>

                </div>

            </div>
        )
    }
}

const mapStateToProps = state => {
    return {
        itineraries: state.itinearies,
        loading: state.itineraries.loading
    }
}

const mapDispatchToProps = dispatch => {
    return {
        fetchItinerariesList: () => dispatch(fetchItinerariesList())
    }
}


export default connect(
    mapStateToProps,
    mapDispatchToProps
)(Itineraries)
