import React, { Component } from 'react';
import { connect } from 'react-redux'
import { fetchActivitiesList } from '../store/actions/activitiesActions'
// import userIcon from '../images/userIcon.png'
// import { Link } from 'react-router-dom';


class Activities extends Component {

    constructor(props) {
        super(props);

        this.state = { 
            // open: false, 
            activities: [],
            itineraries:[],
            fetchActivitiesList:[]
        }
    }

componentDidMount() {
    let itinerary = window.location.pathname.split('/')[0]
    this.props.fetchActivitiesList(itinerary)
}



    //   toggle() {
    //     this.setState({
    //       open: !this.state.open
    //     });
    //   }

      render() {
          const {activities} = this.props;

          console.log(this.props)

          const activitiesList = activities.map(activity => {

         return (
            <div className="activities" key={activity._id}>

                {activity.title}

            </div>
        )

    })

    return(

        <div className="flex-container">
            <div className="activities-list">
                {activitiesList}

            </div>
        </div>
    )
      }

}

const mapStateToProps = state => {
    return {
        activities: state.activities
    }
}

const mapDispatchToProps = dispatch => {
    return {
        fetchActivitiesList: (itinerary) => dispatch (fetchActivitiesList(itinerary))
    }
}

export default connect (
    mapStateToProps,
    mapDispatchToProps
)(Activities)
