import React, { Component } from 'react';



class Cities extends Component {

    constructor(props) {
        super(props);
        this.state = {
            cities: [],
            cityFilter: "",
            filteredCities: []
        }
    }

    componentDidMount() {
        this.fetchCities()
            .then((cities) => this.setState({ cities }))
    }
    componentDidUpdate() {
        this.filterCities();
    }

    handleChange = (e) => {
        this.setState({
            cityFilter: e.target.value
        })
    }

    fetchCities = async () => {
        var url = "https://mern-ubiqum-v2.herokuapp.com/cities/all"
        return await fetch(url, {
            method: "GET",
        })
            .then(response => response.json())
            .then(data => data)
            .catch(error => console.log(error))
    }

    filterCities() {
        const { cities, cityFilter } = this.state;
        let filteredCities = cities.filter((city) =>{
            return city.name.toLowerCase().indexOf(cityFilter.toLowerCase())!== -1 
            },
        ); 
        this.setState({filteredCities})
    }

    render() {
        const { filteredCities } = this.state;

        const cityList = filteredCities.map((city) => {
            return (
                <li className="city" key={city._id}>{city.name}</li>
            )
        }
        )

        return (
            <div>
                <div className="Filter">
                    <label htmlFor="filter">Filter by City: </label>
                    <input type="text" id="filter" 
                        value={this.state.cityFilter}
                        onChange={this.handleChange.bind(this)}/>

                </div>
                <ul className="city-list">
                    {cityList}
                </ul>
            </div>    
        )
    }
}


export default Cities
